function createDropDown(labelText, menuName, optionList) {
    const formattedMenuName = menuName.replace(/\s/g, "");

    const dropDownDiv = document.createElement("div");
    dropDownDiv.classList.add("drop-down-div");

    const dropDownLabel = document.createElement("label");
    dropDownLabel.setAttribute("id", `${formattedMenuName}-label`);
    dropDownLabel.setAttribute("for", `${formattedMenuName}-select`);
    dropDownLabel.textContent = labelText;
    dropDownDiv.appendChild(dropDownLabel);

    const dropDownSelect = document.createElement("select");
    dropDownSelect.setAttribute("id", `${formattedMenuName}-select`);
    dropDownSelect.setAttribute("name", `${formattedMenuName}-select`);
    dropDownDiv.appendChild(dropDownSelect);

    optionList.forEach(option => {
        const newOption = document.createElement("option");
        newOption.classList.add("drop-down-option");
        newOption.setAttribute("value", option);
        newOption.textContent = option;
        dropDownSelect.appendChild(newOption);
    });

    return dropDownDiv;
}

module.exports = createDropDown
